output = []

with open('newnoise.txt', 'r') as papito:
    for line in papito:
        i = 0
        for letter in line:
            i+=1
        if i > 3 and i < 8:
            output.append(line)
    papito.close()

with open('newnoise.txt', 'w') as f:
    for item in output:
        f.write("%s" % item)        
print(output)

